import React from 'react'
import { Helmet } from 'react-helmet';

const Home = () => {


  const [imageUrls] = useState([
    require("../assets/images/banner-1.jpg"),
    require("../assets/images/banner-2.jpg"),
    require("../assets/images/banner-3.jpg"),
  ]);
  const [currentIndex, setCurrentIndex] = useState(0);
  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex(currentIndex => (currentIndex + 1) % imageUrls.length);
    }, 3000);
    return () => clearInterval(intervalId);
  }, [imageUrls]);

  const backgroundImage = imageUrls[currentIndex];


  return (
    <div className='main-container'>
      <Helmet>
        <title>PDC | IIT Gandhinagar</title>
        <meta name="description" content="" />

      </Helmet>
      <header id="head" style={{
          backgroundImage: `url(${backgroundImage})`,
          transition: "1s ease",
          backgroundSize: "cover",
          backgroundAttachment: "fixed",
          backgroundBlendMode: "overlay",
          flexDirection: "column",
          backgroundRepeat: "space",
        }}>
          <div className="banner-content">
            <h1>Sports at IIT Gandhinagar</h1>
            <h2>Where the mind is withour fear!</h2>
          </div>
        </header>

    </div>
  )
}

export default Home
